import React, { useState } from 'react';
import './Portfolio.css';
import TypingText from './TypingText';

const Portfolio = () => {
  const [selectedItem, setSelectedItem] = useState(null);

  const portfolioItems = [
    { id: 1, title: 'Studio Glam', category: 'Studio', description: 'Professional studio makeup services' },
    { id: 2, title: 'Home Service', category: 'Home', description: 'Convenient makeup services at your location' },
    { id: 3, title: 'Birthday Shoot', category: 'Special', description: 'Special occasion makeup for birthdays' },
    { id: 4, title: 'White Wedding', category: 'Wedding', description: 'Elegant wedding makeup services' },
    { id: 5, title: 'Court Wedding', category: 'Wedding', description: 'Intimate court wedding makeup' },
    { id: 6, title: 'Engagement', category: 'Special', description: 'Beautiful engagement makeup looks' }
  ];

  const promotionalTexts = [
    "Professional Makeup Artistry at its Finest",
    "Transform Your Look with Expert Techniques",
    "Perfect for Weddings and Special Events",
    "Customized Makeup for Every Occasion",
    "Experience the GlamByToks Difference"
  ];

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const handleCloseModal = () => {
    setSelectedItem(null);
  };

  return (
    <div className="portfolio-container">
      <div className="portfolio-header">
        <h1>Our Portfolio</h1>
        <p>Discover the artistry and creativity behind GlamByToks</p>
        <TypingText 
          texts={promotionalTexts}
          typingSpeed={100}
          deletingSpeed={50}
          delay={2000}
        />
      </div>

      <div className="portfolio-grid">
        {portfolioItems.map((item) => (
          <div 
            key={item.id} 
            className="portfolio-item"
            onClick={() => handleItemClick(item)}
          >
            <div className="item-content">
              <h3>{item.title}</h3>
              <span className="category">{item.category}</span>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedItem && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <span className="close-button" onClick={handleCloseModal}>&times;</span>
            <div className="modal-info">
              <h2>{selectedItem.title}</h2>
              <p>Category: {selectedItem.category}</p>
              <p>{selectedItem.description}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Portfolio;
